(function($){

	$.fn.sliderA = function(options){

		var defaults = {
				'orientation' : 'left',		//可选 left right 默认left
				'id' : '',
				'className' : '',
				'width' : '',
				'height' : '',
				'num' : 4,					//一屏幕出现的个数  默认4
				'li' : [{'img':'','h4':'','p':'','link':'','title':''}],
				'p' : '',
				'callback' : null
			},

			options = $.extend(defaults, options),

			objs = {
				bar : $('<div class="m_sliderA">'),
				con : $('<ul class="m_sliderACon">'),
				pages : $('<ul class="m_sliderAPage">'),
				btnBar : $('<div class="m_sliderABtn">'),
				btnLeft : $('<a href="javascript:;" class="m_sliderALeft">'),
				btnRight : $('<a href="javascript:;" class="m_sliderARight">')
			};

		dfunc.set = this;
		dfunc.init(options, objs);
	}

	var dfunc = {

		set : '',

		init : function(options, objs){

			var _t = this;

			_t.view(options, objs);
			dfunc.set.html(objs.bar);
		},

		even : function(){

		},

		view : function(options, objs){

			var li = options.li,
				liLen = li.length;

			if(liLen > 0){
				var i = 0, DOM = '';

				for(; i < liLen; i++){

					if(li[i].link == ''){
						li[i].link = 'javascript:;'
					};

					DOM += '<li>' + 
						'<a href="' + li[i].link + '">' +
						'<h4>' + li[i].h4+ '</h4>' +
						'<img src="' + li[i].img +'"/>' +
						'<p>' + li[i].p + '</p>' +
						'</a></li>';
				};
			}

			objs.con.html(DOM);
			objs.bar.append(objs.con);
		}
	}

})(jQuery);